/**
 * Search CSI sections by keyword
 */
export function searchCSISections(query: string, limit = 10): CSISection[] {
  const lowerQuery = query.toLowerCase().trim();

  if (!lowerQuery) {
    return CSI_SECTIONS.slice(0, limit);
  }

  // Score each section based on match quality
  const scored = CSI_SECTIONS.map(section => {
    let score = 0;

    // Exact code match gets highest priority
    if (section.code.toLowerCase().includes(lowerQuery)) {
      score += 1000;
    }

    // Title match
    if (section.title.toLowerCase().includes(lowerQuery)) {
      score += 100;
    }

    // Keyword matches
    section.keywords.forEach(keyword => {
      if (keyword.includes(lowerQuery)) {
        score += 50;
      }
      // Partial word match
      if (lowerQuery.split(' ').some(word => keyword.includes(word) && word.length > 2)) {
        score += 10;
      }
    });

    return { section, score };
  });

  // Filter and sort by score
  return scored
    .filter(item => item.score > 0)
    .sort((a, b) => b.score - a.score)
    .slice(0, limit)
    .map(item => item.section);
}

/**
 * Get section by exact code
 */
export function getSectionByCode(code: string): CSISection | undefined {
  return CSI_SECTIONS.find(s => s.code === code);
}
